SELECT c.id AS car_id, c.parkingspacetype_id, make, model, licenseplate, valettag, status_id, parkingspace_id, color,  u.id AS user_id, firstname, lastname, phone, ps.parkinglot_id
FROM car c JOIN usercar uc ON c.usercar_id = uc.id JOIN users u ON uc.user_id = u.id LEFT JOIN parkingspace ps ON c.parkingspace_id = ps.id WHERE (c.id = $1 OR $1 IS NULL);